function populateNavbar(a, b, c) {
    const navbar = d3.select("#navbarResponsive")
        .append("ul")
        .attr("class", "navbar-nav mx-auto");

    navbar.selectAll("li").data(a).enter()
        .append("li")
        .attr("class", "nav-item")
        .append("a")
        .attr("class", "nav-link js-scroll-trigger")
        .attr("href", function (a) {
            return "#" + a;
        })
        .text(function (a) {
            return a;
        });

    if(b) {
        d3.json("/social.json").then(function (a) {
            const icon = d3.select("#navbarResponsive")
                .append("ul").attr("class", "list-inline  my-1 text-center")
                .selectAll("a")
                .data(a.accounts.filter(function (a) {
                    return -1 < b.indexOf(a.name);
                })).enter()
                .append("a")
                .attr("class", "list-inline-item mx-0")
                .style("color", c)
                .attr("target", "_blank")
                .attr("href", function (a) {
                    return a.url;
                }).attr("title", function (a) {
                    return a.name;
                }).append("span").attr("class", "fa-stack");
            icon.append("span").attr("class", "fa fa-circle fa-stack-2x");
            icon.append("span").attr("class", function (a) {
                return a.icon + " fa-stack-1x fa-inverse";
            });
        });
    }
}

function setHeading(a, b) {
    const c = a.append("div").attr("class", "container")
        .append("div").attr("class", "row")
        .append("div").attr("class", "col-lg-12 text-center");
    c.append("h2").attr("class", "section-heading").text(b);
    c.append("hr").attr("class", "my-4");
}

function authorList(publication, separator) {
    let list = "";
    for (let i = 0; i < publication.authors.length; i++) {
        list += publication.authors[i] + (i < publication.authors.length - 1 ? separator : "");
    }
    return list;
}
