function populateNavbar(titles, social, color) {
    const navbar = d3.select("#navbarResponsive").append("ul").attr("class", "navbar-nav mx-auto");
    navbar.selectAll("li")
        .data(titles)
        .enter()
        .append("li")
        .attr("class", "nav-item")
        .append("a")
        .attr("class", "nav-link js-scroll-trigger")
        .attr("href", a => "#" + a)
        .text(a => a);

    if (social) {
        d3.json("/social.json").then(accounts => {
            const icon = d3.select("#navbarResponsive")
                .append("ul").attr("class", "list-inline  my-1 text-center")
                .selectAll("a")
                .data(accounts.filter(account => -1 < social.indexOf(account.name)))
                .enter()
                .append("a")
                .attr("class", "list-inline-item mx-0")
                .style("color", color)
                .attr("target", "_blank")
                .attr("href", account => account.url)
                .attr("title", account => account.name)
                .append("span").attr("class", "fa-stack");
            icon.append("span").attr("class", "fa fa-circle fa-stack-2x");
            icon.append("span").attr("class", account => account.icon + " fa-stack-1x fa-inverse");
        });
    }
}

function setHeading(a, b) {
    const heading = a.append("div").attr("class", "container")
        .append("div").attr("class", "row")
        .append("div").attr("class", "col-lg-12 text-center");
    heading.append("h2").attr("class", "section-heading").text(b);
    heading.append("hr").attr("class", "my-4");
}