function populateNavbar(a, b, c) {
    const navbar = d3.select("#navbarResponsive")
        .append("ul")
        .attr("class", "navbar-nav mx-auto");

    navbar.selectAll("li").data(a).enter()
        .append("li")
        .attr("class", "nav-item")
        .append("a")
        .attr("class", "nav-link js-scroll-trigger")
        .attr("href", function (a) {
            return "#" + a;
        })
        .text(function (a) {
            return a;
        });

    d3.json("/social.json", function (a) {
        a = d3.select("#navbarResponsive").append("ul").attr("class", "navbar-nav mr-0")
            .style("margin", "10px 0px").selectAll("a").data(a.accounts.filter(function (a) {
                return -1 < b.indexOf(a.name);
            })).enter().append("a").style("color", c).attr("target", "_blank").attr("href", function (a) {
                return a.url;
            }).attr("title", function (a) {
                return a.name;
            }).append("span").attr("class", "fa-stack");
        a.append("span").attr("class", "fa fa-circle fa-stack-2x");
        a.append("span").attr("class", function (a) {
            return a.icon + " fa-stack-1x fa-inverse";
        });
    });
}

function setHeading(a, b) {
    const c = a.append("div").attr("class", "container")
        .append("div").attr("class", "row")
        .append("div").attr("class", "col-lg-12 text-center");
    c.append("h2").attr("class", "section-heading").text(b);
    c.append("hr").attr("class", "my-4");
}